const apiKey="0cfbe8511567c9bdd1c52fe7c460d4ba",weatherBlock=document.querySelector("#weatherBlock");function getWeather(){const e=`http://api.openweathermap.org/data/2.5/weather?q=${document.getElementById("cityInput").value}&units=metric&APPID=${apiKey}&lang=ua`;weatherBlock.style.display="flex",fetch(e).then((e=>e.json())).then((e=>{const t=e.name,n=e.main.temp,o=e.main.pressure,c=e.weather[0].description,d=e.main.humidity,a=e.wind.speed,m=e.wind.deg,r=`http://openweathermap.org/img/w/${e.weather[0].icon}.png`;document.getElementById("cityName").textContent=`Місто: ${t}`,document.getElementById("temperature").textContent=`Температура: ${n}°C`,document.getElementById("pressure").textContent=`Тиск: ${o} hPa`,document.getElementById("description").textContent=`Опис: ${c}`,document.getElementById("humidity").textContent=`Вологість: ${d}%`,document.getElementById("wind").textContent=`Швидкість вітру: ${a} m/s`,document.getElementById("windDirection").textContent=`Напрям вітру: ${m}°`,document.getElementById("weatherIcon").src=r})).catch((e=>{console.error("Помилка запиту:",e),weatherBlock.style.display="none",alert("Не вдалося знайти погодові дані для цього міста.")}))}